    private void jComboBox1ActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_jComboBox1ActionPerformed
        //the country combo box method
        
        String value = jComboBox1.getSelectedItem().toString();
        
        if(value.equals("United Kingdom")){
        jComboBox2.addItem("London");
        jComboBox2.addItem("Oxford");
        jComboBox2.addItem("Southhampton");
        
        //remove other options
        jComboBox2.removeItem("Cape Town");
        jComboBox2.removeItem("Johannesburg");
        jComboBox2.removeItem("Durban");
        } 
        
        else if(value.equals("South Africa")){
        jComboBox2.removeItem("London");
        jComboBox2.removeItem("Oxford");
        jComboBox2.removeItem("Southhampton");
        
         //remove other options
        jComboBox2.addItem("Cape Town");
        jComboBox2.addItem("Johannesburg");
        jComboBox2.addItem("Durban");
        }
        
        
    }//GEN-LAST:event_jComboBox1ActionPerformed

