    private void jButton1ActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_jButton1ActionPerformed
        // TODO add your handling code here:
        
        String Country = jComboBox1.getSelectedItem().toString();
        String Town = " ";
        String Code = " ";
        
        Town = jComboBox2.getSelectedItem().toString();
        
        if(jRadioButton1.isSelected()){
        if(Town.equals("Cape Town")) Code = "021";
        if(Town.equals("Durban")) Code = "031";
        if(Town.equals("Johannesburg")) Code = "011";
        if(Town.equals("London")) Code = "020";
        if(Town.equals("Oxford")) Code = "01865";
        if(Town.equals("Southhampton")) Code = "023";
        JOptionPane.showMessageDialog(null, "Phone code for " + Town + " in " + Country + " is " + Code);
        }
        
        else if(jRadioButton2.isSelected()){
        if(Town.equals("Cape Town")) Code = "8000";
        if(Town.equals("Durban")) Code = "4000";
        if(Town.equals("Johannesburg")) Code = "2000";
        if(Town.equals("London")) Code = "WC2N5DU";
        if(Town.equals("Oxford")) Code = "OX13PN";
        if(Town.equals("Southhampton")) Code = "SO147DW";
        JOptionPane.showMessageDialog(null, "Postal code for " + Town + " in " + Country + " is " + Code);
        }
    }//GEN-LAST:event_jButton1ActionPerformed

